import React, { useState } from 'react';
import axios from 'axios';

export default function App() {
  const [file, setFile] = useState(null);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [subject, setSubject] = useState('');
  const [body, setBody] = useState('');
  const [results, setResults] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) return alert("Please upload an Excel file.");

    const formData = new FormData();
    formData.append("file", file);
    formData.append("sender_email", email);
    formData.append("app_password", password);
    formData.append("subject", subject);
    formData.append("body", body);

    try {
      const res = await axios.post("http://localhost:8000/send-emails/", formData);
      setResults(res.data.results);
    } catch (err) {
  console.error("Sending failed:", err.response?.data || err.message);
  alert("Error sending emails: " + (err.response?.data?.error || err.message));
}

  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-4 bg-white rounded shadow">
      <h1 className="text-2xl font-bold mb-4">Bulk Gmail Mailer</h1>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input type="email" placeholder="Your Gmail" className="w-full border p-2" onChange={e => setEmail(e.target.value)} required />
        <input type="password" placeholder="App Password" className="w-full border p-2" onChange={e => setPassword(e.target.value)} required />
        <input type="text" placeholder="Subject" className="w-full border p-2" onChange={e => setSubject(e.target.value)} required />
        <textarea placeholder="Email Body" className="w-full border p-2" onChange={e => setBody(e.target.value)} required />
        <input type="file" accept=".xlsx" className="w-full" onChange={e => setFile(e.target.files[0])} />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Send Emails</button>
      </form>

      {results.length > 0 && (
        <div className="mt-6">
          <h2 className="text-lg font-semibold">Status:</h2>
          <ul>
            {results.map((r, i) => (
              <li key={i} className={r.status === "Sent" ? "text-green-600" : "text-red-600"}>
                {r.email} - {r.status}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
